import React, { useEffect, useState } from 'react';
import '../../App.css';
import './Exams.css';

import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import CardsExam from '../CardsExam';
import axios from "axios";


export default function Exams() {
  const options = ['Monday', 'Tuesday', 'Thursday',
    'Friday', 'Saturday', 'Sunday']

  const [exams,setExams]=useState({
   loading:false,
   data:null,
   error:false
  });

  const staff = [
    {id: null,
    title: "",
    numQuestions: null,
    level: ""}
   
];

  useEffect(() => {
//     async function fetchData() {
//      try {
//       const {data:response}= await axios.get('http://localhost:8087/api/exam/getList');
//       // const responsejson= await response.json();
     
//       // setExams(responsejson)
//      console.log(response)
//     //  setExams(response.data)
// map
    
//      } catch (error) {
//       console.log(error);
//      }
//     }
//     fetchData();

setExams ({
  loading:true,
   data:null,
   error:false
})
axios.get('http://localhost:8087/api/exam/getList')
  .then(response=>{
    setExams({
      loading:false,
      data:response.data,
      error:false
    })
    console.log(exams)
  })
  .catch(()=>{
    setExams({
      loading:false,
      data:null,
      error:true
    })
  })
  }, [])

  return (
    <div className='content'>
      <h1 >Đề thi</h1>
      <div className='fillter-option'>
        <div className='combobox'>
          <p>
          <Autocomplete
            options={options}
            className='combobox-content'
            renderInput={(params) =>
            <TextField {...params} label="Lớp" variant="outlined" />}
          />
         </p>
         <p>
          <Autocomplete
            options={options}
            className='combobox-content'
            renderInput={(params) =>
              <TextField {...params} label="Môn" variant="outlined" />}
          />
          </p>
        </div>

        <div className='combobox'>
          <p>
          <Autocomplete
            options={options}

            className='combobox-content'
            renderInput={(params) =>
              <TextField {...params} label="Chương" variant="outlined" />}
          />
         </p>
         <p>
          <Autocomplete
            options={options}
            className='combobox-content'
            renderInput={(params) =>
              <TextField {...params} label="Bài" variant="outlined" />}
          />
          </p>
        </div>
           <div>Test la:{exams}</div>   

        <CardsExam></CardsExam>
        
      </div>
      
    </div>
  );
}
